package Components;import java.awt.Color;import java.awt.Dimension;import java.awt.Font;import java.awt.GridLayout;import java.awt.Insets;import javax.swing.BoxLayout;import javax.swing.JButton;import javax.swing.JFrame;import javax.swing.JLabel;import javax.swing.JPanel;import javax.swing.SwingConstants;import javax.swing.border.EmptyBorder;public class JPanelControl extends JPanel{	public JLabel lblName;	public JLabel lblTime;	public JButtonImage btnUndo;	public JButtonImage btnClear;	public JButtonImage btnHint;	public JButtonImage btnMenu;	public JButton btnNumber[];	public JPanel JPanelMenu;	public JPanel JPanelNumber;	public JPanel labelPanel;	public int valClicked;	private final int weight = 200;	private final int height = 500;		public JPanelControl() {		prepareUI();	}		public void prepareUI() {		lblName = new JLabel("SUDOKU", SwingConstants.CENTER);		lblName.setForeground(new Color(255,192,0));		lblName.setFont(new Font("Play",Font.PLAIN,28));				lblTime = new JLabel("00:00", SwingConstants.CENTER);		lblTime.setForeground(Color.WHITE);		lblTime.setFont(new Font("Play",Font.BOLD,24));				btnUndo = new JButtonImage();		btnUndo.createButton("../image/btnUndoFalse.png",75,60);				btnClear = new JButtonImage();		btnClear.createButton("../image/btnClearFalse.png",75,60);				btnMenu = new JButtonImage();		btnMenu.createButton("../image/btnMenuFalse.png",75,60);				btnHint = new JButtonImage();		btnHint.createButton("../image/btnHintFalse.png",75,60);		BoxLayout boxlayout = new BoxLayout(this, BoxLayout.Y_AXIS);		this.setPreferredSize(new Dimension(weight, height));		this.setLayout(boxlayout);		this.setBorder(new EmptyBorder(new Insets(10, 10, 5, 10)));		this.setBackground(new Color(1,26,39));				labelPanel=new JPanel();		labelPanel.setLayout(new GridLayout(2, 1, 0, 10));		labelPanel.setBorder(new EmptyBorder(new Insets(20, 10, 40, 10)));		labelPanel.setBackground(new Color(1,26,39));		labelPanel.add(lblName);		labelPanel.add(lblTime);				JPanelMenu = new JPanel();		JPanelMenu.setLayout(new GridLayout(2, 2, 0, 0));		JPanelMenu.setBackground(new Color(1,26,39));		JPanelMenu.setBorder(new EmptyBorder(new Insets(0, 10, 0, 10)));		JPanelMenu.add(btnUndo);		JPanelMenu.add(btnClear);		JPanelMenu.add(btnMenu);		JPanelMenu.add(btnHint);						JPanelNumber = new JPanel();		btnNumber=new JButton[9];		for(int i=0;i<9;i++) {			btnNumber[i]=new JButton();			btnNumber[i].setText(i+1+"");			btnNumber[i].setForeground(new Color(1,26,39));			btnNumber[i].setBackground(Color.WHITE);			btnNumber[i].setPreferredSize(new Dimension(60,50));			btnNumber[i].setFont(new Font("Play",Font.BOLD,16));			btnNumber[i].setBorderPainted(false);			JPanelNumber.add(btnNumber[i]);		}				JPanelNumber.setLayout(new GridLayout(3, 3, 4, 4));		JPanelNumber.setBackground(new Color(1,26,39));		JPanelNumber.setBorder(new EmptyBorder(new Insets(0, 10, 10, 10)));		JPanelNumber.setSize(200, 200);		this.add(labelPanel);		this.add(JPanelNumber);		this.add(JPanelMenu);			}	public int getValClicked() {		return valClicked;	}	public void setValClicked(int valClicked) {		this.valClicked = valClicked;	}		public static void main(String[] args) {		JFrame frame=new JFrame();		frame.setSize(270, 670);		frame.add(new JPanelControl());		frame.setVisible(true);	}}